import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../../styles/login.css";

export function Login() {
  const [formData, setFormData] = useState({
    username: "",
    password: "",
  });

  const navigate = useNavigate();
  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData({
      username: e.target.value,
      password: e.target.value,
    });
  };

  const onSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    // check is correct,,, we will skip it for now
    e.preventDefault();

    const userCredentials = formData;

    // fetch token
    const fetchToken = async () => {
      const responseToken = await fetch(`http://localhost:5000/auth/`, {
        method: "POST",
        body: JSON.stringify(userCredentials),
        headers: {
          "Content-Type": "application/json",
        },
      });
      const userObject = await responseToken.json();
      console.log(`token is ${userObject.token}`);
      return userObject.token;
    };

    // check token
    const isAuthenticated = async (token: any) => {
      const response = await fetch(`http://localhost:5000/auth/user`, {
        method: "GET",
        headers: {
          "x-auth-token": token,
        },
      });
      const result = await response.json();
      return (result.status = "200");
    };

    const result = await isAuthenticated(fetchToken);

    if (result) {
      navigate("/createPortfolio");
    }
  };

  return (
    <div id="loginContainer">
      <form id="loginForm" onSubmit={onSubmit}>
        <div id="usernameInput">
          <label htmlFor="username">Username: </label>
          <input
            type="text"
            id="username"
            defaultValue=""
            onChange={onChange}
          />
        </div>
        <div id="passwordInput">
          <label htmlFor="password">Password: </label>
          <input
            type="text"
            id="password"
            defaultValue=""
            onChange={onChange}
          />
        </div>
        <input type="submit" value="Login" />
      </form>
    </div>
  );
}
