import { Route, Routes } from "react-router-dom";
import CreatePortfolio from "./components/CreatePortfolio";
import PaperTrade from "./PaperTrade";
import Home from "./components/Home";
import { useEffect, useState } from "react";
import { userID } from "./interfaces";
import React from "react";
import { Navigate } from "react-router";

class App extends React.Component {
  initialState = {
    token: "",
    isLoggedIn: false,
  };
  state = this.initialState;

  // componentDidUpdate() {
  //   if (this.state._id.$oid !== "") {
  //     this.navigate("/dashboard");
  //   }
  // }

  handleLogin(token: string) {
    console.log("im here");
    this.setState({
      isLoggedIn: true,
    });
  }

  render() {
    this.handleLogin = this.handleLogin.bind(this);
    if (this.state.isLoggedIn) {
      this.setState({
        isLoggedIn: false,
      });
      return (
        <Navigate to="/dashboard" replace={true} state={this.state.token} />
      );
    } else {
      return (
        <div>
          <Routes>
            <Route
              path="*"
              element={
                <Home token={this.state.token} handleLogin={this.handleLogin} />
              }
            />
            {/* <Route path="/createportfolio" element={<CreatePortfolio />} /> */}
            <Route
              path="/dashboard"
              element={<PaperTrade token={this.state.token} />}
            />
          </Routes>
        </div>
      );
    }
  }
}

export default App;
