import { ReactElement, useState } from 'react';
import getStockJSON from '../getStockJSON';
import { Portfolio } from '../interfaces';
import StockResultDisplay from './StockResultDisplay';

interface SearchFormProps {
    portfolio: Portfolio
}
const defaultFormData = {
    ticker: "",
  };

function SearchForm(props: SearchFormProps): ReactElement {
    const [formData, setFormData] = useState(defaultFormData);
    const { ticker } = formData;

    const [stockJSON, setStockJSON] = useState<JSON | string>("");

    const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setFormData((prevState) => ({
        ...prevState,
        [e.target.id]: e.target.value,
        }));
        setStockJSON("")
    };

    const onSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        getStockJSON(formData.ticker).then(JSON => {
            console.log(`value is ${JSON}`)
            setStockJSON(JSON)
        })
    };

    return (
        <div id="SearchForm">
            <form onSubmit={onSubmit}>
                <label htmlFor="ticker">Enter a ticker here:</label>
                <input type="text" id="ticker" defaultValue={ticker} onChange={onChange}/>
                <input type="submit" value="Search"/>
            </form>
            <StockResultDisplay portfolio={props.portfolio} JSON={stockJSON}/>

        </div>
    )
}

export default SearchForm;
