      },
    };

    await fetch(`http://localhost:5000/update/${props.userJSON._id}`, {
      method: "POST",
      body: JSON.stringify(editedUser),
      headers: {
        "Content-Type": "application/json",
      },
    });
  };
  return (
    <div id="createPortfolioContainer">
      <form id="createPortfolioForm" onSubmit={onSubmit}>
        <div id="portfolioNameInput">
          <label htmlFor="portfolioName">Your portfolio name: </label>
          <input
            type="text"
            id="portfolioName"
            defaultValue="My First Portfolio"
            onChange={(e) => updateFormData({ portfolioName: e.target.value })}
          />
        </div>
        <div id="cashInput">
          <label htmlFor="cashInput">Starting Cash: </label>
          <input
            type="text"
            id="cashInput"
            defaultValue="100000"
            onChange={(e) => updateFormData({ cash: e.target.value })}
          />
        </div>
        <input type="submit" value="Create" />
      </form>
    </div>
  );
}
