import React, { useEffect, useLayoutEffect, useRef, useState } from "react";
import { Link } from "react-router-dom";
import { getStockLatestPrice } from "../helpers";
import { Stock } from "../interfaces";

interface stockRowProps {
  stock: Stock
  //deleteStock: any
  key: string
}


function StockRow(props: stockRowProps) {
  const[latestPrice, setLatestPrice] = useState(0);
  const[latestPriceFontColor, setLatestPriceFontColor] = useState("black")


  // https://gist.github.com/babakness/faca3b633bc23d9a0924efb069c9f1f5
  // dan abramov set interval typescript version
  type IntervalFunction = () => ( unknown | void )

  function useInterval( callback: IntervalFunction, delay: number ) {

    const savedCallback = useRef<IntervalFunction| null>( null )

    // Remember the latest callback.
    useEffect( () => {
      savedCallback.current = callback
    } )

    // Set up the interval.
    useEffect( () => {
      function tick() {
        if ( savedCallback.current !== null ) {
          savedCallback.current()
        }
      }
      const id = setInterval( tick, delay )
      return () => clearInterval( id )

    }, [ delay ] )
  }

  const fetchPrice = async () => {
    // console.log(`inital is ${newLatestPrice}`)

    getStockLatestPrice(props.stock.symbol).then((newPrice) => {
      setLatestPrice(prevPrice => newPrice)
    })
  }

  useInterval(() => {
    const prevPrice = latestPrice
    getStockLatestPrice(props.stock.symbol).then((newPrice) => {
      setLatestPrice(newPrice)
      // must use new price as setState hook is async
      if (newPrice > latestPrice) {
        setLatestPriceFontColor("green")
        console.log("green")
      } else {
        setLatestPriceFontColor("red")
        console.log("red")
      }
    });
  }, 1000);

  //initial price, runs once
  useLayoutEffect (() => {
    fetchPrice() 
  }, []); 

  // sets latest price every 5 seconds
  // useEffect (() => {
  //   // console.log(`${latestPrice}`)
  //   if (latestPrice[1] > latestPrice[0]) {
  //     setLatestPriceFontColor("green")
  //     // setLatestPrice(newPrice)
  //   } else {
  //     setLatestPriceFontColor("red")
  //     // setLatestPrice(newPrice)
  //   }

  //   const interval = setInterval(() => {
  //     fetchPrice()
  //   }, 1000);
  //   return () => clearInterval(interval);
  // }, []);

  return(
    <tr>
      <td>{props.stock.symbol}</td>
      <td style={{color: latestPriceFontColor}}>{latestPrice}</td>
      <td>{props.stock.quantity}</td>
      <td>{props.stock.pricePurchased}</td>
      <td>{+(latestPrice*props.stock.quantity).toFixed(2)}</td>
      <td>{+(latestPrice*props.stock.quantity-props.stock.pricePurchased*props.stock.quantity).toFixed(2)}</td>
    </tr>
  )
}


interface stockListProps {
  stocks: Stock[]
}
export default function StockList(props: stockListProps) {
  // console.log("stocklist refreshing but why")

 // This method will map out the records on the table
 const StocksToTable = (stockList: Stock[]) => {
  return (
    stockList.map((stock =>
      <StockRow
           stock={stock}
           //deleteStock={() => deleteStock(stock._id)}
           key={stock.symbol}
         />
    )))
 }

 // This following section will display the table with the records of individuals.
 return (
   <div>
     <h3>Stock List</h3>
     <table>
       <thead>
         <tr>
           <th>Symbol</th>
           <th>Latest Price</th>
           <th>Quantity</th>
           <th>Price Purchased</th>
           <th>Value</th>
           <th>Net Value</th>
         </tr>
       </thead>
       <tbody>{StocksToTable(props.stocks)}</tbody>
     </table>
   </div>
 );
}
