import React from 'react';
import { IRootObject, userID, userJSON } from './interfaces';
import "./PaperTrade.css"

import PortfolioDetails from './components/PortfolioDetails';
import SearchForm from './components/forms/SearchForm';
import StockList from './components/stockList';
import Header from './components/header';


import { Route, Routes } from "react-router-dom";
import { getThisUserJSON } from './helpers';

type PaperTradeProps = {
    userJSON: userJSON
}
type PaperTradeState = {
    // userID: userID,
    userJSON?: userJSON
    JSONOrString: IRootObject | string
}

class PaperTrade extends React.Component<PaperTradeProps, PaperTradeState> {

    initialState = {
        userJSON: this.props.userJSON,
        // userJSON: {
        //     "_id": {
        //         "$oid": ""
        //     },
        //     "username": "",
        //     "password": "",
        //     "portfolio": {
        //         "name": "",
        //         "cash": 0
        //     },
        //     "stocks": []
        // },
        JSONOrString: "",
    }
    state = this.initialState;


    componentDidMount() {
        // console.log("papertrade mounted")
        getThisUserJSON(this.props.userJSON._id.$oid).then((userJSON) => {
            if (userJSON) {
                this.setState({
                    userJSON: userJSON,
                })
            }
        })
    };

    componentDidUpdate(prevProps: PaperTradeProps) {
        console.log("helloo component is updating!")
        getThisUserJSON(this.props.userJSON._id.$oid).then((newUserJSON) => {
            console.log(`prev is ${prevProps.userJSON.stocks}`)
            // if (prevProps.userJSON.stocks !== newUserJSON.stocks) {
            //     console.log("they are not the same")
            //     this.setState({
            //         userJSON: newUserJSON,
            //     })
            // }
        })
    };

    handleSearch(JSONOrString: IRootObject | string) {
        if (typeof JSONOrString === 'string') {
            this.setState({
                JSONOrString: JSONOrString,
            })
        } else {
            this.setState({
                JSONOrString: JSONOrString,
            });
        }
    }


    render() {
        this.handleSearch = this.handleSearch.bind(this)

        return(
            <div className="container">
                <Header username={this.state.userJSON.username}/>
                <PortfolioDetails portfolio={this.state.userJSON.portfolio}/>
                <div id="forms">
                    <SearchForm 
                        userJSON={this.state.userJSON}
                        JSONOrString={this.state.JSONOrString} 
                        handleSearch={this.handleSearch}
                    />
                </div>
                <Routes>
                    <Route path="/" element={<StockList stocks={this.state.userJSON.stocks}/>} />
                </Routes>
            </div>
        )
    }
}

export default PaperTrade;
