import { Route, Routes } from "react-router-dom";
import CreatePortfolio from "./components/CreatePortfolio";
import PaperTrade from "./PaperTrade";
import Home from "./components/Home";
import { useEffect, useState } from "react";
import { userID } from "./interfaces";
import React from "react";

class App extends React.Component {
  initialState = {
    token: "",
    newAccount: true,
    isLoggedIn: false,
  };
  state = this.initialState;

  handleLogin(token: string) {
    this.setState({
      token: token,
      newAccount: true,
      isLoggedIn: true,
    });
  }

  render() {
    this.handleLogin = this.handleLogin.bind(this);
    return (
      <div>
        <Routes>
          <Route
            path="*"
            element={
              <Home token={this.state.token} handleLogin={this.handleLogin} />
            }
          />
          {this.state.isLoggedIn &&
            this.state.newAccount(
              <Route
                path="/create"
                element={<CreatePortfolio token={this.state.token} />}
              />
            )}
          {this.state.isLoggedIn && (
            <Route
              path="/dashboard"
              element={<PaperTrade token={this.state.token} />}
            />
          )}
        </Routes>
      </div>
    );
  }
}

export default App;
