import React from 'react';

import { getStockIndex } from './helpers';
import { IRootObject, Portfolio, Stock } from './interfaces';
import "./PaperTrade.css"

import SearchForm from './components/forms/SearchForm';
import PortfolioDetails from './PortfolioDetails';
import PortfolioTable from './PortfolioTable';

import { Route, Routes } from "react-router-dom";

import Navbar from "./components/navbar";
import Edit from "./components/edit";
import Create from "./components/create";
import StockList from './components/stockList';


const defaultPortfolio =  {
    name: "Tech Stocks",
    cash: 1000000,
    stocks: [{
        symbol: "AAPL",
        latestPrice: 132.32,
        quantity: 200,
        pricePurchased: 110.23,
    },
    {
        symbol: "AMZN",
        latestPrice: 3456.32,
        quantity: 100,
        pricePurchased: 2342.32,
    }]
}

type PaperTradeState = {
    portfolio: Portfolio
    JSONOrString: IRootObject | string
    stockJSON?: IRootObject
}

class PaperTrade extends React.Component<{}, PaperTradeState> {
    state={
        portfolio: defaultPortfolio,
        JSONOrString: "",
        stockJSON: undefined,
    }

    handleSearch(JSONOrString: IRootObject | string) {
        if (typeof JSONOrString === 'string') {
            this.setState({
                JSONOrString: JSONOrString,
                stockJSON: undefined,
            })
        } else {
            this.setState({
                JSONOrString: JSONOrString,
                stockJSON: JSONOrString
            });
        }
    }

    handleBuy(cost: number, quantity: number, stockJSON: IRootObject) {
        const portfolio = this.state.portfolio

        const newStock = {
            symbol: stockJSON.symbol,
            latestPrice: stockJSON.latestPrice,
            quantity: quantity,
            pricePurchased: stockJSON.latestPrice,
        }

        const combineTwoStocks = (newStock: Stock, oldStock: Stock) => {
            // quantity will be the sum of both,
            // price purchased will be (q1 * pricepurchased1 + q2 * pricepurchased2)/ (q1+q2)
            // eg: 1 stock at 10$, and you buy 2 stocks at 20$, the average price should become
            // 1*10 = 10, 2*20 = 40, 50/3
            const newTotalQuantity = newStock.quantity + oldStock.quantity;
            const newAveragePricePurchased = 
                (newStock.quantity * newStock.pricePurchased
                + oldStock.quantity * oldStock.pricePurchased)
                / (newTotalQuantity);
            newStock.quantity = newTotalQuantity;
            newStock.pricePurchased = +newAveragePricePurchased.toFixed(2);
            return newStock
        }

        // check sufficient cash
        if (portfolio.cash > cost) {
            portfolio.cash = portfolio.cash - cost;
            // checks if stock exists and merges them if they are
            const index = getStockIndex(portfolio, newStock)
            if (index !== -1) {
                portfolio.stocks[index] = combineTwoStocks(newStock, portfolio.stocks[index])
            } else {
                portfolio.stocks.push(newStock);
            }
            this.setState({
                portfolio: portfolio
            });
        }
    }

    render() {
        this.handleSearch = this.handleSearch.bind(this)
        this.handleBuy = this.handleBuy.bind(this)

        return(
            <div className="container">
                <div id="logo">
                    <a href="http://localhost:3000/">
                        Paper Trade
                    </a>
                </div>
                <div>
                    <Navbar />
                    <Routes>
                        <Route path="/" element={<StockList />} />
                        <Route path="/edit/:id" element={<Edit />} />
                        <Route path="/create" element={<Create />} />
                    </Routes>
                </div>
                <PortfolioDetails portfolio={this.state.portfolio}/>
                {/* <div id="forms">
                    <SearchForm 
                        portfolio={this.state.portfolio}
                        JSONOrString={this.state.JSONOrString} 
                        handleSearch={this.handleSearch}
                        handleBuy={this.handleBuy}/>
                </div> */}

                {/* {this.state.portfolio && <PortfolioTable portfolio={this.state.portfolio}/>} */}
            </div>
        )
    }
}

export default PaperTrade;
