import React, { useEffect, useLayoutEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getStockLatestPrice } from "../helpers";
import { Stock } from "../interfaces";

interface stockRowProps {
  stock: Stock
  //deleteStock: any
  key: string
}


function StockRow(props: stockRowProps) {
  const[latestPrice, setLatestPrice] = useState([0,0]);
  const[latestPriceFontColor, setLatestPriceFontColor] = useState("black")

  const fetchPrice = async () => {
    const newLatestPrice = latestPrice.slice()
    console.log(`inital is ${newLatestPrice}`)

    getStockLatestPrice(props.stock.symbol).then((price) => {
      newLatestPrice.push(price)
      console.log(`before setting latest price to ${newLatestPrice}`)
      newLatestPrice.slice(1,3)
      console.log(`sliced setting latest price to ${newLatestPrice}`)
      setLatestPrice(newLatestPrice)
    });
  }

  // //initial price, runs once
  useLayoutEffect (() => {
    fetchPrice() 
  }, []); 

  // sets latest price every 5 seconds
  useEffect (() => {
    // console.log(`${latestPrice}`)
    if (latestPrice[1] > latestPrice[0]) {
      setLatestPriceFontColor("green")
      // setLatestPrice(newPrice)
    } else {
      setLatestPriceFontColor("red")
      // setLatestPrice(newPrice)
    }

    fetchPrice()

    const interval = setInterval(() => {
      fetchPrice()
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  return(
    <tr>
      <td>{props.stock.symbol}</td>
      <td style={{color: latestPriceFontColor}}>{latestPrice[1]}</td>
      <td>{props.stock.quantity}</td>
      <td>{props.stock.pricePurchased}</td>
      <td>{+(latestPrice[1]*props.stock.quantity).toFixed(2)}</td>
      <td>{+(latestPrice[1]*props.stock.quantity-props.stock.pricePurchased*props.stock.quantity).toFixed(2)}</td>
    </tr>
  )
}


interface stockListProps {
  stocks: Stock[]
}
export default function StockList(props: stockListProps) {
  // console.log("stocklist refreshing but why")

 // This method will map out the records on the table
 const StocksToTable = (stockList: Stock[]) => {
  return (
    stockList.map((stock =>
      <StockRow
           stock={stock}
           //deleteStock={() => deleteStock(stock._id)}
           key={stock.symbol}
         />
    )))
 }

 // This following section will display the table with the records of individuals.
 return (
   <div>
     <h3>Stock List</h3>
     <table>
       <thead>
         <tr>
           <th>Symbol</th>
           <th>Latest Price</th>
           <th>Quantity</th>
           <th>Price Purchased</th>
           <th>Value</th>
           <th>Net Value</th>
         </tr>
       </thead>
       <tbody>{StocksToTable(props.stocks)}</tbody>
     </table>
   </div>
 );
}
