import React, { ReactElement, useEffect, useState } from "react";
import { getStockIndex } from "../helpers";
import { IRootObject, Portfolio } from "../interfaces";

interface BuyAndSellFormProps {
    stockJSON : IRootObject
    portfolio: Portfolio
    handleBuy: (cost: number, quantity: number, stockJSON: IRootObject) => void
}

const defaultFormData = {
    quantity: "100",
  };

function BuyAndSellForm(props: BuyAndSellFormProps): ReactElement {
    const [formData, setFormData] = useState(defaultFormData);

    const { quantity } = formData
    const [cost, setCost] = useState(0);
    const [buyButtonVisibility, setBuyButtonVisibility] = useState("hidden")

    useEffect(() => {
        console.log("use effect triggered")
        if (getStockIndex(props.portfolio, props.stockJSON) !== -1) {
            setBuyButtonVisibility("submit")
        } else {
            setBuyButtonVisibility("hidden")
        }
        setCost(parseInt(quantity)*props.stockJSON.latestPrice)
    }, [props.stockJSON]);

    const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setFormData((prevState) => ({
        ...prevState,
        [e.target.id]: e.target.value,
        }));
        setCost(parseInt(quantity)*props.stockJSON.latestPrice)
    };

    const onSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault()
        props.handleBuy(cost, parseInt(quantity), props.stockJSON)
    }

    return(
        <div id="BuyForm">
            <form onSubmit={onSubmit}>
                <label htmlFor="quantity">Quantity: </label>
                <input type="text" id="quantity" defaultValue="100" onChange={onChange}/>
                <input type="submit" name="buyButton" value="Buy"/>
                <input type={buyButtonVisibility} name="sellButton" value="Sell"/>
            </form>
            <p>Cost / Revenue: {cost.toFixed(2)}</p>
        </div>
    )
}



export default BuyAndSellForm;
