import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getStockJSON } from "../helpers";

function Stock(props) {
  const[latestPrice, setLatestPrice] = useState(0);

  useEffect (() => {
    async function getStockPrice(symbol) {
      const stockJSON = await getStockJSON(symbol);
      return stockJSON.latestPrice
    }
    getStockPrice(props.stock.symbol).then((price) => {
      setLatestPrice(price);
    })
  }, []);

  return(
    <tr>
      <td>{props.stock.symbol}</td>
      <td>{latestPrice}</td>
      <td>{props.stock.quantity}</td>
      <td>{props.stock.pricePurchased}</td>
      <td>{parseFloat(props.stock.latestPrice)*parseInt(props.stock.quantity)}</td>
      <td>{(props.stock.latestPrice*props.stock.quantity)-(props.stock.pricePurchased*props.stock.quantity)}</td>
      {/* <td>
        <Link className="btn btn-link" to={`/edit/${props.stock._id}`}>Edit</Link> |
        <button className="btn btn-link"
          onClick={() => {
            props.deleteStock(props.stock._id);
          }}
        >
          Delete
        </button>
      </td> */}
    </tr>
  )
}

export default function StockList() {
 const [stocks, setStocks] = useState([]);

 // This method fetches the stocks from the database.
 useEffect(() => {
   async function getStocks() {
     const response = await fetch(`http://localhost:5000/stocks/`);

     if (!response.ok) {
       const message = `An error occured: ${response.statusText}`;
       window.alert(message);
       return;
     }

     const stocks = await response.json();
     setStocks(stocks);
   }

   getStocks();

   return;
 });

 // This method will delete a record
 async function deleteStock(id) {
   await fetch(`http://localhost:5000/${id}`, {
     method: "DELETE"
   });

   const newStocks = stocks.filter((el) => el._id !== id);
   setStocks(newStocks);
 }

 // This method will map out the records on the table
 function stockList() {
   return stocks.map((stock) => {
     return (
       <Stock
         stock={stock}
         deleteStock={() => deleteStock(stock._id)}
         key={stock._id}
       />
     );
   });
 }

 // This following section will display the table with the records of individuals.
 return (
   <div>
     <h3>Stock List</h3>
     <table>
       <thead>
         <tr>
           <th>Symbol</th>
           <th>Latest Price</th>
           <th>Quantity</th>
           <th>Price Purchased</th>
           <th>Value</th>
           <th>Net Value</th>
         </tr>
       </thead>
       <tbody>{stockList()}</tbody>
     </table>
   </div>
 );
}
