import { IRootObject, Portfolio, Stock } from "./interfaces";

export async function getStockJSON(ticker: string): Promise<IRootObject | string> {
    console.log(`fetching ${ticker} JSON `);
    try {
        const response = await fetch(`https://sandbox.iexapis.com/stable/stock/${ticker}/quote/
    ?token=Tpk_dec7b122df1e4bce8e1ebe0e6632fc06`)
        const stockJSON = await response.json();
        return stockJSON
    } catch {
        return `Tried to fetch ${ticker}, but the stock wasn't found ):`;
    }
}

export function doesStockExist(portfolio: Portfolio, stock: Stock ): any {
    const isStockSymbol = (stock: Stock) => {
        stock.symbol == stock.symbol;
    }
    const value = portfolio.stocks.findIndex(isStockSymbol)
    console.log(`the boolean is ${value}`)
    return value
}
