import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../../styles/login.css";

export function Login() {
  const [formData, setFormData] = useState({
    username: "",
    password: "",
  });

  const navigate = useNavigate();
  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData({
      username: e.target.value,
      password: e.target.value,
    });
  };

  const onSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    let isAuthenticated = false;
    // check is correct,,, we will skip it for now
    e.preventDefault();

    const userCredentials = formData;

    // fetch token
    const responseToken = await fetch(`http://localhost:5000/auth/`, {
      method: "POST",
      body: JSON.stringify(userCredentials),
      headers: {
        "Content-Type": "application/json",
      },
    });
    const userObject = await responseToken.json();
    console.log(`token is ${userObject.token}`);

    // check credentials
    const response = await fetch(`http://localhost:5000/auth/user`, {
      method: "POST",
      headers: {
        "x-auth-token": userObject.token,
      },
    });
    const result = await response.json();
    console.log(result);
    // console.log(userCredentials);
    // response code = 200
    if (isAuthenticated) {
      navigate("/createPortfolio");
    }
  };

  return (
    <div id="loginContainer">
      <form id="loginForm" onSubmit={onSubmit}>
        <div id="usernameInput">
          <label htmlFor="username">Username: </label>
          <input
            type="text"
            id="username"
            defaultValue=""
            onChange={onChange}
          />
        </div>
        <div id="passwordInput">
          <label htmlFor="password">Password: </label>
          <input
            type="text"
            id="password"
            defaultValue=""
            onChange={onChange}
          />
        </div>
        <input type="submit" value="Login" />
      </form>
    </div>
  );
}
