import { ReactElement, useState } from 'react';
import getStockPrice from './getStockPrice';
import StockResultDisplay from './StockResultDisplay';

const defaultFormData = {
    ticker: "",
  };

function BuyForm(): ReactElement {
    const [formData, setFormData] = useState(defaultFormData);
    const { ticker } = formData;
    const [stockPrice, setStockPrice] = useState<string | null>(null);

    const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setFormData((prevState) => ({
        ...prevState,
        [e.target.id]: e.target.value,
        }));
    };

    const onSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        getStockPrice(formData.ticker).then(value => {
            console.log(`value is ${value}`)
            setStockPrice("30"), () => {
                console.log(`stock price is ${stockPrice}`)
            }

        })
        // setFormData(defaultFormData);
    };

    return (
        <div>
            <form onSubmit={onSubmit} id="searchForm">
                <label htmlFor="ticker">Ticker:</label>
                <br/>
                <input type="text" id="ticker" defaultValue={ticker} onChange={onChange}/>
                <input type="submit" value="Search"/>
            </form>
            {stockPrice && <StockResultDisplay ticker={ticker} price={stockPrice}/>}

        </div>
    )
}

