    <div id="BuyForm">
      <form>
        <label htmlFor="quantity">Quantity: </label>
        <input
          type="text"
          id="quantity"
          defaultValue="100"
          onChange={onChange}
        />
        <input
          type="button"
          disabled={quantity ? false : true}
