import React, { useLayoutEffect, useState } from "react"
import { IRootObject } from "../interfaces";
import StockList from "./stockList"

interface topTenStocksListProps {
    // ...
}

interface stockRowProps {
    stockJSON: IRootObject
}
function StockRow(props: stockRowProps) {
    return(
    <tr>
      <td>{props.stockJSON.symbol}</td>
      <td>{props.stockJSON.companyName}</td>
      <td>{props.stockJSON.latestPrice}</td>
      <td>{props.stockJSON.change}</td>
    </tr>
    )
}

export default function TopTenStocksList() {
    const [topTen, setTopTen] = useState([]);
    const [usePercentChange, setUsePercentChange] = useState(true);

    const fetchData = async () => {
        const request = await fetch(`https://sandbox.iexapis.com/stable/stock/market/list/mostactive/
                                        ?token=Tpk_e2b21cc8584845038c4338a07fc520ef`)
        const data = await request.json()
        setTopTen(data)
    } 

    const TogglePercent = () => {
        return (
            <button type="button" onClick={() => {setUsePercentChange(prev => !prev)}}>
                Toggle %
            </button>
        )
    }

    useLayoutEffect(() => {
        fetchData()
    }, [])

    const StocksToTable = (stockList: IRootObject[]) => {
        return (
          stockList.map((stock =>
            <StockRow stockJSON={stock} key={stock.symbol}/>
          )))
    }

    return(
    <div>
        <div>
            <h3>Top Ten Most Active by Volume</h3>
            <TogglePercent />
        </div>
        <table>
            <thead>
                <tr>
                    <th>Symbol</th>
                    <th>Company Name</th>
                    <th>Latest Price</th>
                    <th>Change</th>
                </tr>
            </thead>
            <tbody>{StocksToTable(topTen)}</tbody>
        </table>
    </div>
    )
}
