import React, { ReactElement, useState } from "react";
import { IRootObject } from "../interfaces";

interface BuyFormProps {
    JSON : IRootObject
}

const defaultFormData = {
    quantity: "100",
  };

function BuyForm(props: BuyFormProps): ReactElement {
    const [formData, setFormData] = useState(defaultFormData);
    const { quantity } = formData
    const [cost, setCost] = useState(parseInt(quantity)*props.JSON.latestPrice);

    const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setFormData((prevState) => ({
        ...prevState,
        [e.target.id]: e.target.value,
        }));
        setCost(parseInt(e.target.value)*props.JSON.latestPrice)
    };

    const onSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault()
        //add stock to the portfolio
        //subtract cash from the portfolio


    }

    return(
        <div id="BuyForm">
            <form onSubmit={onSubmit}>
                <label htmlFor="quantity">Quantity: </label>
                <input type="text" id="quantity" defaultValue="100" onChange={onChange}/>
                <input type="submit" value="Buy"/>
            </form>
            <p>Cost: {cost ? cost : ""}</p>
        </div>
    )
}



export default BuyForm;
