import React, { ReactElement, useState } from 'react';
import BuyForm from './forms/BuyForm';
import SearchForm from './forms/SearchForm';
import StockResultDisplay from './forms/StockResultDisplay';
import getStockJSON from './getStockJSON';
import { IRootObject, Portfolio } from './interfaces';
import "./PaperTrade.css"
import PortfolioDetails from './PortfolioDetails';
import PortfolioTable from './PortfolioTable';


const defaultPortfolio =  {
    name: "Tech Stocks",
    cash: 100000,
    stocks: [{
        ticker: "AAPL",
        latestPrice: 132.32,
        quantity: 200,
        pricePurchased: 110.23,
    },
    {
        ticker: "AMZN",
        latestPrice: 3456.32,
        quantity: 100,
        pricePurchased: 2342.32,
    }]
}

type PaperTradeState = {
    portfolio: Portfolio
    JSONOrString: IRootObject | string
    stockJSON?: IRootObject
}

class PaperTrade extends React.Component<{}, PaperTradeState> {
    state={
        portfolio: defaultPortfolio,
        JSONOrString: "hello",
        stockJSON: undefined,
    }



    handleSearch(JSONOrString: IRootObject | string) {
        if (typeof JSONOrString === 'string') {
            this.setState({
                JSONOrString: JSONOrString,
            });
        } else {
            this.setState({
                stockJSON: JSONOrString
            });
        }
    }

    handleBuy(cost: number) {
        const portfolio = this.state.portfolio
        portfolio.cash = portfolio.cash - cost
        this.setState({
            portfolio: 0,
        });
    }

    render() {
        this.handleSearch = this.handleSearch.bind(this)
        this.handleBuy = this.handleBuy.bind(this)
        if (this.state.stockJSON) {
            const { stockJSON } = this.state.stockJSON
        }
        return(
        <div className="container">
            <div id="logo">
                <a href="http://localhost:3000/">
                    Paper Trade
                </a>
            </div>
            <PortfolioDetails portfolio={this.state.portfolio}/>
            <div id="forms">
                <SearchForm JSONOrString={this.state.JSONOrString} handleSearch={this.handleSearch}/>
                {this.state.stockJSON && <StockResultDisplay stockJSON={this.state.stockJSON}/>}
                {this.state.stockJSON && <BuyForm 
                portfolio={this.state.portfolio} 
                stockJSON={this.state.stockJSON} 
                handleBuy={this.handleBuy}/>}

            </div>

            {this.state.portfolio && <PortfolioTable portfolio={this.state.portfolio}/>}
        </div>
        )
    }
}

export default PaperTrade;
