async function getStockPrice(ticker: string): Promise<number | Error> {
    console.log(`fetching ${ticker} price `);
    try {
        const response = await fetch(`https://sandbox.iexapis.com/stable/stock/${ticker}/quote/?token=Tpk_dec7b122df1e4bce8e1ebe0e6632fc06`)
        const stockJSON = await response.json();
        const stockLatestPrice = await stockJSON.latestPrice;
        return stockLatestPrice
    } catch {
        throw new Error (`tried to fetch ${ticker}, but stock not found`);
    }
}

export default getStockPrice;
