import React, { useEffect, useLayoutEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getStockLatestPrice } from "../helpers";
import { Stock } from "../interfaces";

interface stockProps {
  stock: Stock
  //deleteStock: any
  key: number
}


function StockRow(props: stockProps) {
  const[latestPrice, setLatestPrice] = useState(0);
  const[latestPriceFontColor, setLatestPriceFontColor] = useState("black")
  console.log(`latest price is ${latestPrice}`)

  //initial price, runs once
  useLayoutEffect (() => {
    console.log("useLayout effect triggered")
    getStockLatestPrice(props.stock.symbol).then((price) => {
      setLatestPrice(price)
    });
  }, []);

  // sets latest price every 5 seconds
  useEffect (() => {
    console.log("useeffect triggered")
    const interval = setInterval(() => {
      getStockLatestPrice(props.stock.symbol).then((newPrice) => {
        if (newPrice > latestPrice) {
          setLatestPriceFontColor("green")
        } else {
          setLatestPriceFontColor("red")
        }
        setLatestPrice(newPrice);
        })
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  return(
    <tr>
      <td>{props.stock.symbol}</td>
      <td style={{color: latestPriceFontColor}}>{latestPrice}</td>
      <td>{props.stock.quantity}</td>
      <td>{props.stock.pricePurchased}</td>
      <td>{latestPrice*props.stock.quantity}</td>
      <td>{latestPrice*props.stock.quantity-props.stock.pricePurchased*props.stock.quantity}</td>
      {/* <td>
        <Link className="btn btn-link" to={`/edit/${props.stock._id}`}>Edit</Link> |
        <button className="btn btn-link"
          onClick={() => {
            props.deleteStock(props.stock._id);
          }}
        >
          Delete
        </button>
      </td> */}
    </tr>
  )
}


interface stockListProps {
  stocks: Stock[]
}
export default function StockList(props: stockListProps) {

 // This method will delete a record
//  async function deleteStock(id: any) {
//    await fetch(`http://localhost:5000/${id}`, {
//      method: "DELETE"
//    });

//    const newStocks = stocks.filter((el) => el._id !== id);
//    setStocks(newStocks);
//  }

 // This method will map out the records on the table
 const StocksToTable = (stockList: Stock[]) => {
  return (
    stockList.map((stock =>
      <StockRow
           stock={stock}
           //deleteStock={() => deleteStock(stock._id)}
           key={stock._id}
         />
    )))
 }

 // This following section will display the table with the records of individuals.
 return (
   <div>
     <h3>Stock List</h3>
     <table>
       <thead>
         <tr>
           <th>Symbol</th>
           <th>Latest Price</th>
           <th>Quantity</th>
           <th>Price Purchased</th>
           <th>Value</th>
           <th>Net Value</th>
         </tr>
       </thead>
       <tbody>{StocksToTable(props.stocks)}</tbody>
     </table>
   </div>
 );
}
