import React, { ReactElement, useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router";
import { Link } from "react-router-dom";
import { IRootObject, Portfolio, userJSON } from "../../interfaces";

interface BuyAndSellFormProps {
    userJSON: userJSON
    stockJSON : IRootObject
    // handleBuy: (cost: number, quantity: number, stockJSON: IRootObject) => void
}


export default function BuyAndSellForm(props: BuyAndSellFormProps){
    // const [formData, setFormData] = useState({
    //     // "_id": {
    //     //     "$oid": props.userJSON._id
    //     //   },
    //     //   "username": props.userJSON.username,
    //     //   "password": props.userJSON.password,
    //     //   "portfolio": props.userJSON.portfolio,
    //     //   "stocks": props.userJSON.stocks,
    //     symbol: props.stockJSON.symbol,
    //     quantity: "100",
    //     pricePurchased: props.stockJSON.latestPrice,
    // })
    console.log(`user id is ${props.userJSON._id}`)

    const [quantity, setQuantity] = useState(100)

    const [cost, setCost] = useState(0);
    // const [sellButtonVisibility, setSellButtonVisibility] = useState(false)

    // const params = useParams();
    // const navigate = useNavigate();

    // console.log(props.userJSON._id.$oid)
    // console.log(params.id)


    // useEffect(() => {
    //     async function fetchData() {
    //         // const id = params?.id?.toString();
    //         const response = await fetch(`http://localhost:5000/users/${props.userJSON._id.$oid}`);
    //         if (!response.ok) {
    //             const message = `An error has occured: ${response.statusText}`;
    //             window.alert(message);
    //             return;
    //         }

    //         const user = await response.json();
    //         if (!user) {
    //             window.alert(`user with id ${props.userJSON._id.$oid} not found`);
    //             navigate("/");
    //             return;
    //         }

    //         // setFormData(user);
    //     }

    //     fetchData();

    //     return;
    // }, [params.id, navigate]);

    // function updateFormData(value: any) {
    //     return setFormData((prev: any) => {
    //       return { ...prev, ...value };
    //     });
    //   }

    useEffect(() => {
        console.log("use effect triggered")
        setCost(quantity*props.stockJSON.latestPrice)
    });

    const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setQuantity(parseInt(e.target.value))
        setCost(parseInt(e.target.value)*props.stockJSON.latestPrice)
    };

    async function onSubmit(e: React.FormEvent<HTMLFormElement>) {
        e.preventDefault()

        if (cost > props.userJSON.portfolio.cash) {
            window.alert("You don't have enough cash to buy this stock!")
            return;
        }

        const newStocks = props.userJSON.stocks.slice()

        const newStock = {
            symbol: props.stockJSON.symbol,
            quantity: quantity,
            pricePurchased: props.stockJSON.latestPrice,
        }

        newStocks.push(newStock)

        const newCash = props.userJSON.portfolio.cash - cost;
        const newPortfolio = {
            name: props.userJSON.portfolio.name,
            cash: newCash,
        }

        const editedUser = {
            "portfolio": newPortfolio,
            "stocks": newStocks
          };

        console.log(editedUser)

        await fetch(`http://localhost:5000/update/${props.userJSON._id}`, {
            method: "POST",
            body: JSON.stringify(editedUser),
            headers: {
            'Content-Type': 'application/json'
            },
        });
        console.log("submitting buy form")
    }

    return(
        <div id="BuyForm">
            <form onSubmit={onSubmit}>
                <label htmlFor="quantity">Quantity: </label>
                <input type="text" id="quantity" defaultValue="100" onChange={onChange}/>
                <input type="submit" name="buyButton" value="Buy"/>
                {/* <input type="button" hidden={sellButtonVisibility} value="Sell" onClick={() => onSell()}/> */}
                {/* <Link className="btn btn-link" to={`/edit/${props.userJSON._id}`}>Edit</Link> */}
            </form>
            <p>Cost / Revenue: {cost.toFixed(2)}</p>
        </div>
    )
}
